import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useData } from '../context/DataContext';
import { UserRole, PurchaseOrder, Supplier, Product } from '../types';
import { 
  Package, 
  Plus, 
  Clock, 
  CheckCircle, 
  Truck,
  AlertCircle,
  Edit,
  Eye
} from 'lucide-react';

const OrdersPage: React.FC = () => {
  const { user, hasRole } = useAuth();
  const { 
    purchaseOrders, 
    suppliers, 
    products, 
    locations,
    addPurchaseOrder,
    updatePurchaseOrder 
  } = useData();

  const [showCreateForm, setShowCreateForm] = useState(false);
  const [selectedSupplier, setSelectedSupplier] = useState<string>('');
  const [orderItems, setOrderItems] = useState<Array<{
    productId: string;
    quantity: number;
    unitPrice: number;
  }>>([]);

  // Для поставщика - только просмотр своих заказов
  if (hasRole(UserRole.SUPPLIER)) {
    const supplierOrders = purchaseOrders.filter(order => 
      order.supplierId === user?.id
    );

    return (
      <div className="space-y-6">
        <div className="card bg-gradient-to-r from-honey-500 to-honey-600 text-white">
          <h1 className="text-xl font-semibold mb-2">Мои заказы</h1>
          <p className="text-honey-100">Заказы от New Format</p>
        </div>

        <div className="space-y-4">
          {supplierOrders.length === 0 ? (
            <div className="card text-center py-8">
              <Package className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600">У вас пока нет заказов</p>
            </div>
          ) : (
            supplierOrders.map(order => {
              const location = locations.find(l => l.id === order.locationId);

              return (
                <div key={order.id} className="card">
                  <div className="flex items-center justify-between mb-4">
                    <div>
                      <h3 className="font-semibold">Заказ #{order.id.slice(-4)}</h3>
                      <p className="text-sm text-gray-600">{location?.name}</p>
                    </div>
                    <span className={`px-3 py-1 rounded-full text-xs font-medium ${
                      order.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                      order.status === 'confirmed' ? 'bg-blue-100 text-blue-800' :
                      order.status === 'shipped' ? 'bg-purple-100 text-purple-800' :
                      order.status === 'delivered' ? 'bg-green-100 text-green-800' :
                      'bg-red-100 text-red-800'
                    }`}>
                      {order.status === 'pending' && 'Ожидает подтверждения'}
                      {order.status === 'confirmed' && 'Подтвержден'}
                      {order.status === 'shipped' && 'Отправлен'}
                      {order.status === 'delivered' && 'Доставлен'}
                      {order.status === 'cancelled' && 'Отменен'}
                    </span>
                  </div>

                  <div className="space-y-2 mb-4">
                    {order.items.map((item, index) => {
                      const product = products.find(p => p.id === item.productId);
                      return (
                        <div key={index} className="flex justify-between text-sm">
                          <span>{product?.name}</span>
                          <span>{item.quantity} {product?.unit} × {item.unitPrice} ₽ = {item.totalPrice} ₽</span>
                        </div>
                      );
                    })}
                  </div>

                  <div className="flex items-center justify-between pt-4 border-t border-gray-200">
                    <div className="text-sm text-gray-600">
                      Заказ от: {order.orderDate.toLocaleDateString('ru-RU')}
                    </div>
                    <div className="font-semibold">
                      Итого: {order.totalAmount.toLocaleString()} ₽
                    </div>
                  </div>

                  {order.status === 'pending' && (
                    <div className="flex space-x-2 mt-4">
                      <button
                        onClick={() => updatePurchaseOrder(order.id, { status: 'confirmed' })}
                        className="flex-1 btn-primary text-sm"
                      >
                        Подтвердить
                      </button>
                      <button
                        onClick={() => updatePurchaseOrder(order.id, { status: 'cancelled' })}
                        className="flex-1 btn-secondary text-sm"
                      >
                        Отклонить
                      </button>
                    </div>
                  )}

                  {order.status === 'confirmed' && (
                    <button
                      onClick={() => updatePurchaseOrder(order.id, { status: 'shipped' })}
                      className="w-full btn-primary text-sm mt-4"
                    >
                      Отметить как отправленный
                    </button>
                  )}
                </div>
              );
            })
          )}
        </div>
      </div>
    );
  }

  // Для администратора - управление заказами
  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending': return <Clock className="w-4 h-4 text-yellow-500" />;
      case 'confirmed': return <CheckCircle className="w-4 h-4 text-blue-500" />;
      case 'shipped': return <Truck className="w-4 h-4 text-purple-500" />;
      case 'delivered': return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'cancelled': return <AlertCircle className="w-4 h-4 text-red-500" />;
      default: return <Clock className="w-4 h-4 text-gray-500" />;
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'pending': return 'Ожидает подтверждения';
      case 'confirmed': return 'Подтвержден';
      case 'shipped': return 'Отправлен';
      case 'delivered': return 'Доставлен';
      case 'cancelled': return 'Отменен';
      default: return 'Неизвестно';
    }
  };

  const addOrderItem = () => {
    if (selectedSupplier) {
      const supplier = suppliers.find(s => s.id === selectedSupplier);
      if (supplier && supplier.products.length > 0) {
        setOrderItems(prev => [...prev, {
          productId: supplier.products[0].productId,
          quantity: supplier.products[0].minOrderQuantity,
          unitPrice: supplier.products[0].purchasePrice,
        }]);
      }
    }
  };

  const updateOrderItem = (index: number, field: string, value: any) => {
    setOrderItems(prev => prev.map((item, i) => 
      i === index ? { ...item, [field]: value } : item
    ));
  };

  const removeOrderItem = (index: number) => {
    setOrderItems(prev => prev.filter((_, i) => i !== index));
  };

  const createOrder = () => {
    if (!selectedSupplier || orderItems.length === 0) return;

    const supplier = suppliers.find(s => s.id === selectedSupplier);
    if (!supplier) return;

    const totalAmount = orderItems.reduce((sum, item) => sum + (item.quantity * item.unitPrice), 0);

    const newOrder = {
      locationId: 'loc1', // По умолчанию первая локация
      supplierId: selectedSupplier,
      items: orderItems.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
        unitPrice: item.unitPrice,
        totalPrice: item.quantity * item.unitPrice,
      })),
      totalAmount,
      status: 'pending' as const,
      orderDate: new Date(),
      expectedDelivery: new Date(Date.now() + 3 * 24 * 60 * 60 * 1000), // +3 дня
      notes: `Заказ от администратора`,
    };

    addPurchaseOrder(newOrder);
    setShowCreateForm(false);
    setSelectedSupplier('');
    setOrderItems([]);
    alert('Заказ успешно создан!');
  };

  return (
    <div className="space-y-6">
      {/* Заголовок */}
      <div className="card bg-gradient-to-r from-honey-500 to-honey-600 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-xl font-semibold mb-2">Заказы поставщикам</h1>
            <p className="text-honey-100">Управление заказами и поставками</p>
          </div>
          <button
            onClick={() => setShowCreateForm(true)}
            className="btn-secondary"
          >
            <Plus className="w-4 h-4 mr-2" />
            Новый заказ
          </button>
        </div>
      </div>

      {/* Статистика */}
      <div className="grid grid-cols-2 gap-4">
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Всего заказов</p>
              <p className="text-2xl font-bold text-gray-900">{purchaseOrders.length}</p>
            </div>
            <Package className="w-8 h-8 text-blue-500" />
          </div>
        </div>
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">В ожидании</p>
              <p className="text-2xl font-bold text-yellow-600">
                {purchaseOrders.filter(o => o.status === 'pending').length}
              </p>
            </div>
            <Clock className="w-8 h-8 text-yellow-500" />
          </div>
        </div>
      </div>

      {/* Список заказов */}
      <div className="space-y-4">
        {purchaseOrders.length === 0 ? (
          <div className="card text-center py-8">
            <Package className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600">Заказов пока нет</p>
            <button
              onClick={() => setShowCreateForm(true)}
              className="btn-primary mt-4"
            >
              Создать первый заказ
            </button>
          </div>
        ) : (
          purchaseOrders.map(order => {
            const supplier = suppliers.find(s => s.id === order.supplierId);
            const location = locations.find(l => l.id === order.locationId);

            return (
              <div key={order.id} className="card">
                <div className="flex items-center justify-between mb-4">
                  <div>
                    <h3 className="font-semibold">Заказ #{order.id.slice(-4)}</h3>
                    <p className="text-sm text-gray-600">
                      {supplier?.name} → {location?.name}
                    </p>
                  </div>
                  <div className="flex items-center space-x-2">
                    {getStatusIcon(order.status)}
                    <span className="text-sm font-medium">
                      {getStatusText(order.status)}
                    </span>
                  </div>
                </div>

                <div className="space-y-2 mb-4">
                  {order.items.map((item, index) => {
                    const product = products.find(p => p.id === item.productId);
                    return (
                      <div key={index} className="flex justify-between text-sm">
                        <span>{product?.name}</span>
                        <span>{item.quantity} {product?.unit} × {item.unitPrice} ₽ = {item.totalPrice} ₽</span>
                      </div>
                    );
                  })}
                </div>

                <div className="flex items-center justify-between pt-4 border-t border-gray-200">
                  <div className="text-sm text-gray-600">
                    Заказ от: {order.orderDate.toLocaleDateString('ru-RU')}
                    {order.expectedDelivery && (
                      <span className="ml-4">
                        Ожидается: {order.expectedDelivery.toLocaleDateString('ru-RU')}
                      </span>
                    )}
                  </div>
                  <div className="font-semibold">
                    Итого: {order.totalAmount.toLocaleString()} ₽
                  </div>
                </div>

                {order.notes && (
                  <div className="mt-3 p-2 bg-gray-50 rounded text-sm text-gray-600">
                    {order.notes}
                  </div>
                )}
              </div>
            );
          })
        )}
      </div>

      {/* Модальное окно создания заказа */}
      {showCreateForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-2xl mx-4 max-h-[90vh] overflow-y-auto">
            <h3 className="text-lg font-semibold mb-4">Создать заказ</h3>

            {/* Выбор поставщика */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Поставщик
              </label>
              <select
                value={selectedSupplier}
                onChange={(e) => setSelectedSupplier(e.target.value)}
                className="input-field"
              >
                <option value="">Выберите поставщика</option>
                {suppliers.map(supplier => (
                  <option key={supplier.id} value={supplier.id}>
                    {supplier.name}
                  </option>
                ))}
              </select>
            </div>

            {/* Товары заказа */}
            {selectedSupplier && (
              <div className="mb-4">
                <div className="flex items-center justify-between mb-2">
                  <label className="block text-sm font-medium text-gray-700">
                    Товары
                  </label>
                  <button
                    onClick={addOrderItem}
                    className="btn-secondary text-sm py-1 px-3"
                  >
                    <Plus className="w-4 h-4 mr-1" />
                    Добавить
                  </button>
                </div>

                <div className="space-y-3">
                  {orderItems.map((item, index) => {
                    const supplier = suppliers.find(s => s.id === selectedSupplier);
                    const availableProducts = supplier?.products || [];
                    const selectedProduct = availableProducts.find(p => p.productId === item.productId);
                    const product = products.find(p => p.id === item.productId);

                    return (
                      <div key={index} className="flex items-center space-x-2 p-3 bg-gray-50 rounded-lg">
                        <select
                          value={item.productId}
                          onChange={(e) => updateOrderItem(index, 'productId', e.target.value)}
                          className="flex-1 input-field text-sm"
                        >
                          {availableProducts.map(supplierProduct => {
                            const product = products.find(p => p.id === supplierProduct.productId);
                            return (
                              <option key={supplierProduct.productId} value={supplierProduct.productId}>
                                {product?.name} - {supplierProduct.purchasePrice} ₽/{product?.unit}
                              </option>
                            );
                          })}
                        </select>

                        <input
                          type="number"
                          value={item.quantity}
                          onChange={(e) => updateOrderItem(index, 'quantity', parseFloat(e.target.value) || 0)}
                          step="0.1"
                          min={selectedProduct?.minOrderQuantity || 0}
                          className="w-20 input-field text-sm text-center"
                          placeholder="Кол-во"
                        />

                        <span className="text-sm text-gray-600 min-w-[60px]">
                          {product?.unit}
                        </span>

                        <span className="text-sm font-medium min-w-[80px]">
                          {item.quantity * item.unitPrice} ₽
                        </span>

                        <button
                          onClick={() => removeOrderItem(index)}
                          className="text-red-500 hover:text-red-700"
                        >
                          ×
                        </button>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Кнопки */}
            <div className="flex space-x-3">
              <button
                onClick={() => setShowCreateForm(false)}
                className="flex-1 btn-secondary"
              >
                Отмена
              </button>
              <button
                onClick={createOrder}
                disabled={!selectedSupplier || orderItems.length === 0}
                className="flex-1 btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Создать заказ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrdersPage;
